/** * Created by Hoshiko on 5/28/15. */public class MyObject{    private String name;    private int age;    public MyObject(String name, int age)    {        this.name = name;        this.age = age;    }    public String getName()    {        return this.name;    }    public int getAge()    {        return this.age;    }    public void setName(String name)    {        this.name = name;    }    public void setAge(int age)    {        this.age = age;    }    public boolean sameObj(MyObject obj1, MyObject obj2){        if ((obj1.getName()).equals(obj2.getName())&& obj1.getAge()==obj2.getAge()){            return true;        }else {            return false;        }    }    public void main(String[] args) {        MyObject obj1 = new MyObject("Bobby", 12);        MyObject obj2 = new MyObject("Bobby", 12);        MyObject obj3 = new MyObject("Bobby", 14);        MyObject obj4 = new MyObject("Suzy", 12);        MyObject obj5 = new MyObject("Suzy", 14);        System.out.println(sameObj(obj1, obj2));    }}